import React, { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';
import styled from 'styled-components';
import TemCard from './Tem/TemCard'
import background from './IMG/tetem.jpg'

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  background: rgba(125, 125, 125, .45);
  height: 200vh;
  width: 100%;
  background: url(${background}) no-repeat fixed center;

`;

const ContentContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 2%;
`;

const SearchInput = styled.input`
  width: 50%;
  left: 25%;
  text-align: center;
  position: fixed;
  color: royalblue;
  border: 2px solid royalblue;
  height: 3vh;
  letter-spacing: 2px;

  &::placeholder {
    color: royalblue; 

  } 
`;


function App() {
  const [temtem, setTemTem] = useState();
  const [search, setSearch] = useState('')
  const [searchResults, setSearchResults] = useState()
  useEffect( () => {
    axios.get(`https://temtem-api.mael.tech/api/temtems/`)
    .then(res => {
      setTemTem(res.data)
      setSearchResults(res.data)
      console.log('temtem', res)})

    .catch(err=> console.log('from app err', err) 
    )
  }, [])

  useEffect (() => {
    axios.get(`https://temtem-api.mael.tech/api/techniques
    `)
    .then(res => {

      console.log('types', res)})
    .catch(err=> console.log('from app err', err) 
    )
  },[])

  useEffect(() => {
    if(temtem){
      const results = temtem.filter(temtem => {
        return temtem.name.toLowerCase().includes(search.toLowerCase())//compare character to search
        //types are at types[0]
      }) //arr of results that is subset arr w/ matching serach

      return setSearchResults(results)
    }

  }, [search]);

  const onChange = (e) => {
    setSearch(e.target.value)
  }

  if(!searchResults){
    return <div>loading...</div>
  }
  return (
    <MainContainer>
      <SearchInput placeholder="Search for temtem" onChange={onChange}></SearchInput >
      <ContentContainer>
        {/* <TemCard name={temtem.name} image={temtem.wikiPortraitUrlLarge} type={temtem.types} description={temtem.gameDescription}/> */}
        {searchResults.map((obj, index) => {
          return <TemCard name={obj.name} image={obj.wikiPortraitUrlLarge} type={obj.types} key={index}/>
        })}
      </ContentContainer>
    </MainContainer>
  )
}

export default App;
