import React from 'react'
import styled from 'styled-components'

const CardContainer = styled.div`
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    height: 100vw;
    width: 100vw;
    border: 1px solid red;
    background: cadetblue;
    background: slategrey;
    background-size: contain;
`;

const CardHeading = styled.h3`
    font-size: 2rem;
    width: 25%;
    background: white;
    border: 1px solid white;
`;

const CardHeader = styled.img`
    height: 25%;
    width: 25%;
    margin: 1%;
    box-shadow: 5px 5px 3px 3px rgba(76, 234, 231);
    background: url('https://progameguides.com/wp-content/uploads/2020/01/featured-temtem-digital-types-list.jpg');
    background-position-x: -150px;
    background-size: contain;
`;



const TemCard = ({ name, image, type }) => {
    return (
    <CardContainer>

        <CardHeader src={image} alt="temtem" />
        <CardHeading>{name}</CardHeading>
        {type.reduce((el, add) => {return <span>{`${el} ${add}`} </span>})}
            
    </CardContainer>
    )
}

export default TemCard
