import React from 'react'
import styled from 'styled-components'

const CardContainer = styled.div`
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    height: 150px;
    width: 150px;
    /* box-shadow: 5px 5px rgba(175, 175, 125, .4); */
    box-shadow: ${props => props.digital ? 'grey' : 'white'}
    background: cadetblue;
    background: rgba(76, 234, 231, .3);
    background-size: contain;
    margin: .5%;

    span {
        color: white;
        margin: 1%;
    }
`;

const CardDescription = styled.div`
    display: flex;



`;

const CardDescriptionDiv = styled.div`
    display: flex;
    flex-direction: column;
    width: 70%;
    padding: 1%;
    line-height: 1.5;
    color: white;
    font-size: 1.5rem;

    p {
        font-size: 2rem;
        padding: 0 0 1% 0;
    }
`;

const CardHeading = styled.h3`
    display: flex;
    justify-content: space-between; 
    font-size: 2rem;
    width: 25%;
    color: white;
    margin-left: 1%;
`;

const CardHeader = styled.img`
    max-height: 105px;
    width: 105px;
    margin: 1% auto 0 auto;
    box-shadow: 2px 2px 3px 3px rgba(76, 234, 231);
    background: rgba(175, 175, 125, .4);
    /* background: url('https://progameguides.com/wp-content/uploads/2020/01/featured-temtem-digital-types-list.jpg');
    background-position-x: -150px;
    background-size: contain; */
`;



const TemCard = ({ name, image, type, description }) => {
    return (
    <CardContainer>
        <CardDescription>
            <CardHeader src={image} alt="temtem" />
        </CardDescription>
        <CardHeading> {name} </CardHeading>
        <span>Type(s): {type.reduce((el, add) => {return <span>{`${el} ${add}`} </span>})}</span>
    </CardContainer>
    )
}

export default TemCard
